import argparsefrom pathlib import Pathfrom gallery_inspector.export import export_images_tablefrom gallery_inspector.generate import generate_images_tableparser = argparse.ArgumentParser(description="Gallery inspector")parser.add_argument('--folder', type=str, required=True, help='Path to the image folder')parser.add_argument('--output', type=str, required=True, help='Path to the outputs')args = parser.parse_args()target_path = Path(args.folder)df = generate_images_table(target_path)export_images_table(df, Path(args.output) / "images_table.xlsx")